mapper.select:
select
    t.id as `key`,
    t.app_name as `value`
from nl_app_info t


        and t.id > #{lastKey}



        and t.app_name like #{matchValue_pattern}


order by t.id asc
limit #{limit}

mapper.delete:
delete from nl_app_relation_tablespace_info
where app_id=#{appId,jdbcType=VARCHAR} and tablespace_id in

    #{_id}
